.video-toolbar .ops .watchlater .text,
.video-toolbar-v1 .toolbar-left .watchlater .text {
    display: none;
}
}
.video-toolbar .ops .watchlater .tip,
.video-toolbar-v1 .toolbar-left .watchlater .tip {
  position: absolute;
  top: calc(100% + 8px);
  left: 50%;
  transform: translateX(-50%);
  z-index: 1000;
  background: rgba(0, 0, 0, 0.8666666667);
  padding: 4px 8px;
  border-radius: 4px;
  color: #eee;
  transition: all 0.2s ease-out;
  opacity: 0;
  pointer-events: none;
}
.video-toolbar .ops .watchlater .tip.show,
.video-toolbar-v1 .toolbar-left .watchlater .tip.show {
  opacity: 1;
  pointer-events: initial;
}
.video-toolbar .ops .watchlater .be-icon,
.video-toolbar-v1 .toolbar-left .watchlater .be-icon {
  display: inline-flex;
}
@media (min-width: 1681px) {
.video-toolbar .ops .watchlater .be-icon,
.video-toolbar-v1 .toolbar-left .watchlater .be-icon {
    --size: 36px !important;
}
}
.video-toolbar-v1 .watchlater .be-icon {
